mapper.insert:
INSERT INTO VISITOR(IP,FUNNEL,DATE)
(SELECT #{param1} AS IP,'revisit' AS FUNNEL,NOW() AS DATE
FROM VISITOR 
WHERE EXISTS (SELECT * FROM VISITOR WHERE DATE BETWEEN DATE_SUB(NOW(),INTERVAL 1 HOUR) AND NOW() AND IP = #{param1}) AND IP = #{param1}
ORDER BY DATE DESC
LIMIT 1)
UNION ALL
(SELECT #{param1} AS IP, #{param2} AS FUNNEL, NOW() AS DATE
FROM VISITOR
WHERE NOT EXISTS (SELECT * FROM VISITOR WHERE DATE BETWEEN DATE_SUB(NOW(),INTERVAL 1 HOUR) AND NOW() AND IP = #{param1}) AND IP = #{param1}
ORDER BY DATE DESC
LIMIT 1
);
